                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-gray-200">
                {filteredClients.map((client) => (
                  <tr key={client.id}>
                    <td className="px-6 py-4 whitespace-normal break-words">
                      <div className="text-sm font-medium text-gray-900 break-words">{client.name && client.name.trim() !== '' ? client.name : 'Nome nÃ£o informado'}</div>
                    </td>
                    <td className="px-6 py-4 whitespace-normal break-words">
